const Button = ({
  title,
  label = "",
  pVertical = "6",
  pHorizontal = "10",
  bgColor = "bg-[#A4074D]",
  textStyle = "text-white text-2xl leading-6 font-inter tracking-[0.4px]",
  borderThickness,
  borderStyle,
  borderColor,
}: {
  title: string;
  label?: string;
  pVertical?: string;
  pHorizontal?: string;
  bgColor?: string;
  borderThickness?: string;
  borderStyle?: string;
  borderColor?: string;
  textStyle?: string;
}) => {
  const px = `px-${pHorizontal}`;
  const py = `py-${pVertical}`;
  const border =
    borderThickness && borderStyle && borderColor
      ? `border-${borderThickness} border-[#${borderColor}] border-${borderStyle}`
      : "border-none";
  return (
    <button
      type="button"
      className={`${px} ${bgColor} ${border} ${textStyle} ${py} rounded-2xl`}
      aria-label={label}
    >
      {title}
    </button>
  );
};

